function Response(data, stream){
    this.idStream = stream._id;
    this.id = data.id;
    this.stream = stream;
    this.write = this.send;
}

Response.prototype = {
    send: function(message){
        message = message || {};
        if( !message.result && !message.error ){
            message.result = {};
        }

        message.id = this.id;
        this.stream.write( JSON.stringify(message) );
    }
}

module.exports = Response;

